import{_ as e,c as i,o as t,a}from"./app.4844cc51.js";const n="/docs/assets/evaluation-config-overview.d011f3e4.jpg",o="/docs/assets/tier-incentives-overview.68c7b83a.jpg",s="/docs/assets/full-evaluation-overview.8101ea02.jpg",_=JSON.parse('{"title":"Evaluation Configurations","description":"","frontmatter":{},"headers":[{"level":2,"title":"What the Admin Sees","slug":"what-the-admin-sees","link":"#what-the-admin-sees","children":[{"level":3,"title":"Evaluation Roles and Categories","slug":"evaluation-roles-and-categories","link":"#evaluation-roles-and-categories","children":[]},{"level":3,"title":"Level + Tier Based Incentives","slug":"level-tier-based-incentives","link":"#level-tier-based-incentives","children":[]}]}],"relativePath":"admin/evaluation-configurations.md"}'),r={name:"admin/evaluation-configurations.md"},l=a('<h1 id="evaluation-configurations" tabindex="-1">Evaluation Configurations <a class="header-anchor" href="#evaluation-configurations" aria-hidden="true">#</a></h1><p><img src="'+n+'" alt="Evaluation Config Overview"><em>Visual representation of the evaluation configuration interface.</em></p><h2 id="what-the-admin-sees" tabindex="-1">What the Admin Sees <a class="header-anchor" href="#what-the-admin-sees" aria-hidden="true">#</a></h2><p>The Evaluation Configurations page allows administrators to set up and manage evaluation systems for users within the Learning Platform. This dashboard enables the creation of evaluation roles, categories, and types with associated points, which will later be reflected in user evaluations. The page also includes options for configuring level and tier-based incentives.</p><h3 id="evaluation-roles-and-categories" tabindex="-1">Evaluation Roles and Categories <a class="header-anchor" href="#evaluation-roles-and-categories" aria-hidden="true">#</a></h3><ul><li><strong>Evaluation Role List</strong>: A section displaying existing evaluation roles (e.g., &quot;Course Evaluator&quot;, &quot;Attendance Monitor&quot;), with options to add new roles.</li><li><strong>Add Category Form</strong>: A form to create categories, such as: <ul><li>Category Name: &quot;Attendance&quot;</li><li>Description: &quot;Tracks user participation in sessions.&quot;</li></ul></li><li><strong>Add Types and Points</strong>: Within each category, the admin can define types and assign points, for example: <ul><li>Type: &quot;Perfect&quot; (100 points)</li><li>Type: &quot;Good&quot; (75 points)</li><li>Type: &quot;Needs Improvement&quot; (50 points)</li></ul></li><li><strong>Category Overview</strong>: A table showing all categories, their types, and assigned points.</li></ul><h3 id="level-tier-based-incentives" tabindex="-1">Level + Tier Based Incentives <a class="header-anchor" href="#level-tier-based-incentives" aria-hidden="true">#</a></h3><p><img src="'+o+'" alt="Tier Incentives Overview"><em>Visual representation of the tier-based incentives configuration.</em></p><ul><li><strong>Level Structure</strong>: A list of levels (e.g., Level 1, Level 2, Level 3), each with configurable tiers.</li><li><strong>Tier Details</strong>: For each level, tiers with incentives, such as: <ul><li>Level 1, Tier 1: &quot;Certificate&quot; (500 points required)</li><li>Level 2, Tier 2: &quot;Bonus Day Off&quot; (1000 points required)</li></ul></li><li><strong>Add/Edit Incentives</strong>: Buttons to add new tiers or edit existing ones, including incentive type (e.g., Certificate, Bonus) and point threshold.</li><li><strong>Incentive Summary</strong>: A display of all levels, tiers, and their incentives for quick reference.</li></ul><p><img src="'+s+'" alt="Full Evaluation Overview"><em>Comprehensive view of the evaluation and incentive configuration.</em></p><p>This page serves as a centralized tool for administrators to configure evaluation criteria and incentive structures, which will be visible to users in their evaluation profiles (details to be explained later).</p>',11),u=[l];function c(d,v,h,g,p,f){return t(),i("div",null,u)}const w=e(r,[["render",c]]);export{_ as __pageData,w as default};
