import{_ as o,c as t,d as i,a as e,o as s}from"./app.4844cc51.js";const r="/docs/assets/online-courses-grid-view.6fb47472.jpg",n="/docs/assets/online-courses-table-view.dd051a36.jpg",_=JSON.parse('{"title":"Online Courses","description":"","frontmatter":{},"headers":[{"level":2,"title":"What the Admin Sees","slug":"what-the-admin-sees","link":"#what-the-admin-sees","children":[{"level":3,"title":"Course Overview","slug":"course-overview","link":"#course-overview","children":[]},{"level":3,"title":"Create Course","slug":"create-course","link":"#create-course","children":[]},{"level":3,"title":"Course Modules","slug":"course-modules","link":"#course-modules","children":[]}]}],"relativePath":"admin/online-courses.md"}'),l={name:"admin/online-courses.md"},a=e('<h1 id="online-courses" tabindex="-1">Online Courses <a class="header-anchor" href="#online-courses" aria-hidden="true">#</a></h1><div style="display:flex;flex-direction:column;gap:20px;"><img src="'+r+'" alt="Online Courses Grid View"><img src="'+n+'" alt="Online Courses Table View"></div>',2),u=e('<h2 id="what-the-admin-sees" tabindex="-1">What the Admin Sees <a class="header-anchor" href="#what-the-admin-sees" aria-hidden="true">#</a></h2><p>The Online Courses page allows administrators to manage all online courses within the Learning Platform. This dashboard offers flexible viewing options and tools to create and configure courses, including modules with video or PDF content. The page displays the following:</p><h3 id="course-overview" tabindex="-1">Course Overview <a class="header-anchor" href="#course-overview" aria-hidden="true">#</a></h3><ul><li><strong>View Options</strong>: Toggle between Grid View and Table View to display all online courses. <ul><li><strong>Grid View</strong>: Shows course thumbnails with titles (e.g., &quot;Advanced JavaScript&quot;) and brief statuses (e.g., &quot;Active&quot;).</li><li><strong>Table View</strong>: Lists courses with columns for Course Name, Difficulty Level, Duration, and Status.</li></ul></li><li><strong>Search and Filter</strong>: Options to search by course name or filter by status (e.g., Active, Draft) or difficulty level (e.g., Beginner, Intermediate).</li><li><strong>Action Buttons</strong>: Edit or delete options for each course, with a confirmation prompt.</li></ul><h3 id="create-course" tabindex="-1">Create Course <a class="header-anchor" href="#create-course" aria-hidden="true">#</a></h3><ul><li><strong>Course Information Form</strong>: <ul><li><strong>Course Name</strong>*: Required field (e.g., &quot;Introduction to Python&quot;).</li><li><strong>Course Description</strong>: Optional text (e.g., &quot;Learn Python basics&quot;).</li><li><strong>Difficulty Level</strong>*: Required dropdown (e.g., Beginner, Intermediate, Advanced).</li><li><strong>Duration (minutes)</strong>: Numeric field (e.g., 120 minutes).</li><li><strong>Course Status</strong>: Dropdown (e.g., Active, Draft, Archived).</li><li><strong>Course Image</strong>: Upload field for an image to represent the course (e.g., &quot;python_intro.jpg&quot;).</li></ul></li><li><strong>Submit Button</strong>: Saves the course with a confirmation (e.g., &quot;Course created at 04:15 PM EEST, October 14, 2025&quot;).</li></ul><h3 id="course-modules" tabindex="-1">Course Modules <a class="header-anchor" href="#course-modules" aria-hidden="true">#</a></h3><ul><li><strong>Module List</strong>: Displays all modules for the selected course, with options to add new modules.</li><li><strong>Add Module Form</strong>: <ul><li><strong>Module Name</strong>*: Required field (e.g., &quot;Module 1: Basics&quot;).</li><li><strong>Duration (minutes)</strong>: Numeric field (e.g., 60 minutes).</li><li><strong>Module Description</strong>: Optional text (e.g., &quot;Covers Python fundamentals&quot;).</li><li><strong>Required Module</strong>: Checkbox to mark as mandatory.</li><li><strong>Active Module</strong>: Checkbox to enable/disable visibility.</li></ul></li><li><strong>Module Content</strong>: <ul><li><strong>Content Type</strong>*: Required dropdown (e.g., Video, PDF).</li><li><strong>Content Title</strong>*: Required field (e.g., &quot;Python Basics Video&quot;).</li><li><strong>If Video</strong>: <ul><li><strong>Select Video</strong>*: Upload field for video file (e.g., &quot;basics_video.mp4&quot;) or Google Drive URL.</li></ul></li><li><strong>If PDF</strong>: <ul><li><strong>PDF Source</strong>*: Upload field for PDF file (e.g., &quot;basics.pdf&quot;) or Google Drive URL.</li><li><strong>PDF Page Count</strong>*: Required numeric field (e.g., 15 pages), to be counted manually for accurate reading time estimates.</li></ul></li><li><strong>Required Content</strong>: Checkbox to mark as mandatory.</li><li><strong>Active Content</strong>: Checkbox to enable/disable visibility.</li></ul></li><li><strong>Add Content Button</strong>: Saves the module content with a confirmation (e.g., &quot;Content added at 04:20 PM EEST, October 14, 2025&quot;).</li></ul>',8);function d(c,g,h,m,f,p){return s(),t("div",null,[a,i(" *Visual representations of the grid and table views for online courses.* "),u])}const C=o(l,[["render",d]]);export{_ as __pageData,C as default};
