import{_ as e,c as t,o as a,a as s}from"./app.4844cc51.js";const n="/docs/assets/user-performance-overview.586cd8a4.jpg",o="/docs/assets/evaluation-form-overview.0ed0f48a.jpg",r="/docs/assets/assessment-summary-overview.0d59b513.jpg",_=JSON.parse('{"title":"User Performance Evaluation","description":"","frontmatter":{},"headers":[{"level":2,"title":"What the Admin Sees","slug":"what-the-admin-sees","link":"#what-the-admin-sees","children":[{"level":3,"title":"Department Selection and User Overview","slug":"department-selection-and-user-overview","link":"#department-selection-and-user-overview","children":[]},{"level":3,"title":"Evaluation Process","slug":"evaluation-process","link":"#evaluation-process","children":[]},{"level":3,"title":"Overall Assessment","slug":"overall-assessment","link":"#overall-assessment","children":[]}]}],"relativePath":"admin/user-performance-evaluation.md"}'),i={name:"admin/user-performance-evaluation.md"},l=s('<h1 id="user-performance-evaluation" tabindex="-1">User Performance Evaluation <a class="header-anchor" href="#user-performance-evaluation" aria-hidden="true">#</a></h1><p><img src="'+n+'" alt="User Performance Overview"><em>Visual representation of the user performance evaluation interface.</em></p><h2 id="what-the-admin-sees" tabindex="-1">What the Admin Sees <a class="header-anchor" href="#what-the-admin-sees" aria-hidden="true">#</a></h2><p>The User Performance Evaluation page allows administrators to assess user performance based on their completed courses and departmental data. This dashboard provides tools to evaluate users and assign ratings.</p><h3 id="department-selection-and-user-overview" tabindex="-1">Department Selection and User Overview <a class="header-anchor" href="#department-selection-and-user-overview" aria-hidden="true">#</a></h3><ul><li><strong>Department Dropdown</strong>: A menu to select a department (e.g., R&amp;D, HR), showing all users within that department.</li><li><strong>User List</strong>: A table displaying users in the selected department, including names, roles (e.g., L1-Employee), and a list of completed courses (e.g., &quot;Advanced Python - Completed on October 12, 2025&quot;).</li><li><strong>Course Details</strong>: For each user, details of finished courses, such as course name, completion date, and duration.</li></ul><h3 id="evaluation-process" tabindex="-1">Evaluation Process <a class="header-anchor" href="#evaluation-process" aria-hidden="true">#</a></h3><p><img src="'+o+'" alt="Evaluation Form Overview"><em>Visual representation of the evaluation form.</em></p><ul><li><strong>Evaluation Form</strong>: The admin can evaluate users based on categories and types defined in the Evaluation Configurations page (e.g., Attendance - Perfect: 100 points).</li><li><strong>Rating Options</strong>: A scale or dropdown to rate the user (e.g., Perfect, Good, Needs Improvement) with corresponding points.</li><li><strong>Comments Section</strong>: A text box for the admin to add optional comments (e.g., &quot;Excellent participation in all sessions&quot;).</li><li><strong>Submit Evaluation</strong>: A button to save the evaluation, recording the rating, points, and comments with a timestamp (e.g., &quot;Evaluated at 03:15 PM EEST, October 14, 2025&quot;).</li></ul><h3 id="overall-assessment" tabindex="-1">Overall Assessment <a class="header-anchor" href="#overall-assessment" aria-hidden="true">#</a></h3><p><img src="'+r+'" alt="Assessment Summary Overview"><em>Visual representation of the overall assessment summary.</em></p><ul><li><strong>Overall Assessment</strong>: A summary of the user&#39;s performance across all evaluations.</li><li><strong>Summary with Level + Tier Based Incentive Calculation</strong>: Displays the user&#39;s current level (e.g., Level 2) and tier (e.g., Tier 1), with incentives calculated based on points from the Evaluation Configurations page (e.g., &quot;Certificate awarded at 500 points&quot;).</li><li><strong>Total Score</strong>: The cumulative points from all evaluations (e.g., &quot;Total Score: 750 points&quot;).</li><li><strong>Incentive Amount</strong>: The incentive earned, calculated from the configured thresholds (e.g., &quot;Incentive: Bonus Day Off at 1000 points&quot;).</li><li><strong>Additional Information</strong>: Includes completion dates, comments, and a history of evaluations (e.g., &quot;Last evaluated on October 13, 2025, 02:00 PM EEST&quot;).</li></ul><p>This page enables administrators to evaluate user performance, assign ratings, and review detailed assessments with incentive outcomes.</p>',13),d=[l];function c(u,m,h,p,v,g){return a(),t("div",null,d)}const w=e(i,[["render",c]]);export{_ as __pageData,w as default};
