import{_ as e,c as i,o as t,a as o}from"./app.4844cc51.js";const p=JSON.parse('{"title":"Using Audio Courses","description":"","frontmatter":{},"headers":[{"level":2,"title":"Accessing Audio Course Content","slug":"accessing-audio-course-content","link":"#accessing-audio-course-content","children":[]},{"level":2,"title":"Listening to Audio Content","slug":"listening-to-audio-content","link":"#listening-to-audio-content","children":[]},{"level":2,"title":"Progress Tracking","slug":"progress-tracking","link":"#progress-tracking","children":[]},{"level":2,"title":"Viewing Audio Course Details","slug":"viewing-audio-course-details","link":"#viewing-audio-course-details","children":[]},{"level":2,"title":"Completing an Audio Course","slug":"completing-an-audio-course","link":"#completing-an-audio-course","children":[]}],"relativePath":"user/audio-courses.md"}'),a={name:"user/audio-courses.md"},s=o('<h1 id="using-audio-courses" tabindex="-1">Using Audio Courses <a class="header-anchor" href="#using-audio-courses" aria-hidden="true">#</a></h1><h2 id="accessing-audio-course-content" tabindex="-1">Accessing Audio Course Content <a class="header-anchor" href="#accessing-audio-course-content" aria-hidden="true">#</a></h2><ul><li>Audio courses allow users to learn through audio materials, such as narrated lessons or podcasts.</li><li>To begin, navigate to the Audio Courses section.</li><li>Select an available audio course from the list.</li><li>Each audio course includes details such as title, description, duration, and availability status.</li></ul><h2 id="listening-to-audio-content" tabindex="-1">Listening to Audio Content <a class="header-anchor" href="#listening-to-audio-content" aria-hidden="true">#</a></h2><ul><li>Click the Play button to start listening to the audio course.</li><li>You can pause, resume, or seek to specific timestamps within the audio track.</li><li>The system automatically tracks your listening progress.</li></ul><h2 id="progress-tracking" tabindex="-1">Progress Tracking <a class="header-anchor" href="#progress-tracking" aria-hidden="true">#</a></h2><ul><li>Listening progress is updated regularly to show how much of the audio you\u2019ve completed.</li><li>Time spent listening is recorded periodically to track engagement accurately.</li><li>If you stop listening midway, your last position is saved automatically. When you return, playback resumes from where you left off.</li></ul><h2 id="viewing-audio-course-details" tabindex="-1">Viewing Audio Course Details <a class="header-anchor" href="#viewing-audio-course-details" aria-hidden="true">#</a></h2><p>While listening, you can view all course details in the Audio Section, including:</p><ul><li>Course title and description</li><li>Total duration</li><li>Instructor name (if available)</li><li>Progress percentage</li><li>Remaining time</li><li>Related materials (e.g., transcripts or summaries, if provided)</li></ul><h2 id="completing-an-audio-course" tabindex="-1">Completing an Audio Course <a class="header-anchor" href="#completing-an-audio-course" aria-hidden="true">#</a></h2><ul><li>Listen to the entire audio course until the end to mark it as complete.</li><li>Once finished, the course status will automatically update to &quot;Complete Course&quot;.</li><li>After completion, you may be asked to provide feedback by: <ul><li>Submitting a rating for the audio course.</li><li>Adding a comment (minimum of 10 letters) to share your thoughts or suggestions.</li></ul></li></ul>',12),n=[s];function l(r,u,c,d,h,g){return t(),i("div",null,n)}const f=e(a,[["render",l]]);export{p as __pageData,f as default};
