import{_ as e,c as i,o as a,a as s}from"./app.4844cc51.js";const m=JSON.parse('{"title":"Using Courses","description":"","frontmatter":{},"headers":[{"level":2,"title":"Viewing Available Courses","slug":"viewing-available-courses","link":"#viewing-available-courses","children":[]},{"level":2,"title":"Enrolling in a Course","slug":"enrolling-in-a-course","link":"#enrolling-in-a-course","children":[]},{"level":2,"title":"Participating in a Course","slug":"participating-in-a-course","link":"#participating-in-a-course","children":[]},{"level":2,"title":"Completing a Course","slug":"completing-a-course","link":"#completing-a-course","children":[]},{"level":2,"title":"Taking Quizzes","slug":"taking-quizzes","link":"#taking-quizzes","children":[]}],"relativePath":"user/courses.md"}'),t={name:"user/courses.md"},l=s('<h1 id="using-courses" tabindex="-1">Using Courses <a class="header-anchor" href="#using-courses" aria-hidden="true">#</a></h1><h2 id="viewing-available-courses" tabindex="-1">Viewing Available Courses <a class="header-anchor" href="#viewing-available-courses" aria-hidden="true">#</a></h2><ul><li>Users can view all available courses, including: <ul><li><strong>Public Courses</strong>: Accessible to all users.</li><li><strong>Private Courses</strong>: Only visible to users who are assigned or belong to the course&#39;s designated group.</li></ul></li><li>Courses are listed with details such as name, description, start date, end date, and availability status.</li></ul><h2 id="enrolling-in-a-course" tabindex="-1">Enrolling in a Course <a class="header-anchor" href="#enrolling-in-a-course" aria-hidden="true">#</a></h2><ul><li>Select a course from the available list.</li><li>Choose a specific session time that suits your schedule from the available options.</li><li>Click the &quot;Enroll Now&quot; button to register for the selected session.</li><li>Upon enrollment, a notification message is automatically sent to your manager to inform them of your enrollment in the course.</li></ul><h2 id="participating-in-a-course" tabindex="-1">Participating in a Course <a class="header-anchor" href="#participating-in-a-course" aria-hidden="true">#</a></h2><ul><li><strong>Clock In/Clock Out</strong>: <ul><li>Each time you attend a course session, you must perform a &quot;Clock In&quot; at the start of the session.</li><li>When you finish the session, perform a &quot;Clock Out&quot; to record the end of your participation.</li></ul></li><li>Attendance is tracked, and the duration is calculated based on the time between clock-in and clock-out.</li></ul><h2 id="completing-a-course" tabindex="-1">Completing a Course <a class="header-anchor" href="#completing-a-course" aria-hidden="true">#</a></h2><ul><li>Participate in all scheduled sessions as per the course availability details.</li><li>Once the course is completed, provide feedback by: <ul><li>Submitting a <strong>rating</strong> to evaluate the course.</li><li>Adding a <strong>comment</strong> with a minimum of 10 letters to share your thoughts or suggestions.</li></ul></li></ul><h2 id="taking-quizzes" tabindex="-1">Taking Quizzes <a class="header-anchor" href="#taking-quizzes" aria-hidden="true">#</a></h2><ul><li>After completing a course, an administrator will create a quiz to assess your understanding.</li><li>Quizzes will be made available for you to attempt.</li><li>You have up to <strong>3 attempts</strong> to complete the quiz.</li><li>On your final attempt, regardless of success or failure, you will see the correct answers and your score.</li></ul>',11),o=[l];function r(n,u,c,d,h,g){return a(),i("div",null,o)}const f=e(t,[["render",r]]);export{m as __pageData,f as default};
